// Create a new context for managing customers
import React, { createContext, useState, useContext } from 'react';

const AllcustContext = createContext();

export const AllcustData = ({ children }) => {
  const [customers, setCustomers] = useState([]);

  return (
    <AllcustContext.Provider value={[customers, setCustomers]}>
      {children}
    </AllcustContext.Provider>
  );
};

export const useAllcust = () => useContext(AllcustContext);
