import React, { useState, useEffect, useContext } from 'react';
// import Pagination from './Pagination';
// import Search from './Search';
import Data from '../apis/Data';
import { useAllcust} from '../context/AllCust';

const Custmers= (props)=> {
  // const [customers, setCustomers] = useState([]);
  // const [currentPage, setCurrentPage] = useState(1);
  // const [totalPages, setTotalPages] = useState(1);
  // const [searchTerm, setSearchTerm] = useState('');

  // Fetch customers based on current page and search term
  // eslint-disable-next-line react-hooks/exhaustive-deps
  const [customers, setCustomers]=useContext(useAllcust)
  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await Data.get("/");
            const { data } = response.data; // Destructure data property from the response
            setCustomers(data.records); // Set the records array as the customers state
          } catch (error) {
            console.error("Error fetching data:", error);
          }
    };

    fetchData();
}, []);


 
  return (
    <div className="container">
      <h1 className="my-4">All Customers</h1>
     
      <table className="table table-striped">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Customer Name</th>
            <th>Age</th>
            <th>Phone</th>
            <th>Location</th>
            <th>Date</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          
        </tbody>
      </table>
      
    </div>
  );
}

export default Custmers;
