import React, { useState, useEffect } from 'react';
import Pagination from './Pagination';
import Search from './Search';

function Custmers() {
  const [customers, setCustomers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');

  // Fetch customers based on current page and search term
  useEffect(() => {
    const fetchCustomers = async () => {
      // Fetch data from backend API using fetch or axios
      const response = await fetch(`/api/v1/all?page=${currentPage}&searchTerm=${searchTerm}`);
      const data = await response.json();
      setCustomers(data.records);
      setTotalPages(data.totalPages);
    };

    fetchCustomers();
  }, [currentPage, searchTerm]);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    // Automatically set the page to 1 when performing a new search
    setCurrentPage(1);
  };

  return (
    <div className="container">
      <h1 className="my-4">All Customers</h1>
      <Search onSearch={handleSearch} />
      <table className="table table-striped">
        <thead>
          <tr>
            <th>S.No</th>
            <th>Customer Name</th>
            <th>Age</th>
            <th>Phone</th>
            <th>Location</th>
            <th>Date</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          <td>23</td>
          {customers.map(customer => (
            <tr key={customer.sno}>
              <td>{customer.sno}</td>
              <td>{customer.customer_name}</td>
              <td>{customer.age}</td>
              <td>{customer.phone}</td>
              <td>{customer.location}</td>
              <td>{customer.date}</td>
              <td>{customer.time}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />
    </div>
  );
}

export default Custmers;
